import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/core/services/auth/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  login: FormGroup | any
  constructor(private http:HttpClient, private route:Router, private auth: AuthService) { }

  ngOnInit(): void {
    this.login=new FormGroup({
      'email':new FormControl(),
      'password':new FormControl()

    })
  }

  onSubmit(){
   this.auth.onSubmit(this.login) //Appel de la méthode de connexion
  }

}
<div class="wrapper bg-white mt-5">
  <div class="h3 text-center">CASH MANAGEMENT</div>
  <div class="h4 text-muted text-center pt-2">Entrez vos informations de connexion</div>
  <form class="pt-2" [(formGroup)="login" >
      <div class="form-group py-2">
          <div class="input-field"> <span class="far fa-user p-2"></span>
            <input type="text" placeholder="Username or Email Address" required class="" formControlName="email">
          </div>
      </div>
      <div class="form-group py-1 pb-2">
          <div class="input-field">
            <span class="fas fa-lock p-2"></span>
            <input type="password" placeholder="Enter your Password" required class="" formControlName="password">
            <button class="btn bg-white text-muted"><span class="far fa-eye-slash"></span></button>
          </div>
      </div>
      <div class="d-flex align-items-start">
          <div class="ml-auto">
            <a href="#" id="forgot">Mot de passe oublié?</a>
          </div>
      </div>
      <button class="btn btn-block text-center my-3" (click)="onSubmit()">Connexion</button>
      <div class="text-center pt-3 text-muted">Pas un membre? <a href="#">S'inscrire</a></div>
  </form>
</div>
