import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor(private http: HttpClient, private route: Router) { }
  onSubmit(login:any){

    this.http.get<any>("http://localhost:3000/auth/login")
    .subscribe(res=>{
      lo
     const user = res.find((a:any)=>{
